import { h } from "preact";
import { useMemo, useState } from "preact/hooks";
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend
} from "recharts";

export default function Dashboard({ state }) {
  const [metric, setMetric] = useState("revenue");
  const data = useMemo(
    () => [
      { name: "Mon", revenue: 120, users: 42 },
      { name: "Tue", revenue: 188, users: 59 },
      { name: "Wed", revenue: 164, users: 54 },
      { name: "Thu", revenue: 205, users: 71 },
      { name: "Fri", revenue: 242, users: 88 }
    ],
    []
  );

  return (
    <section style={{ padding: "12px", fontFamily: "ui-sans-serif, system-ui" }}>
      <h2 style={{ marginTop: 0 }}>Renderify Dashboard Demo</h2>
      <p style={{ color: "#475569" }}>LLM TSX -> Preact Runtime -> Recharts</p>
      <div style={{ display: "flex", gap: "8px", marginBottom: "12px" }}>
        <button type="button" onClick={() => setMetric("revenue")}>Revenue</button>
        <button type="button" onClick={() => setMetric("users")}>Users</button>
      </div>
      <div style={{ width: "100%", height: 260 }}>
        <ResponsiveContainer>
          <LineChart data={data}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey={metric} stroke={metric === "revenue" ? "#0f766e" : "#1d4ed8"} strokeWidth={3} />
          </LineChart>
        </ResponsiveContainer>
      </div>
      <p style={{ marginBottom: 0 }}>Selected metric: {metric} / server count: {state?.count ?? 0}</p>
    </section>
  );
}
